import {MatchDetailCard} from '../components/MatchDetailCard';
import {MatchSmallCard} from '../components/MatchSmallCard';
import {React,useEffect,useState} from 'react';
import {useParams} from 'react-router-dom';
import './TeamPage.css';

export function TeamPage() {
    const [team,setTeam] = useState({});
    const {teamName} = useParams();
    useEffect(
    ()=>{
        const getTeamData = async()=>{
            const response = await fetch(`http://localhost:8084/team/teamName/${teamName}`);
            const data = await response.json();
            setTeam(data);
        }

        getTeamData();
    },[teamName]
    )

    if(!team.teamName)
    return (
        <h1>Team Not Found!!!</h1>
        );

    else
    return (
        <div className="TeamPage">
          <h2>{team.teamName}</h2>
          <h2>{team.totalMatches}</h2>
          <h2>{team.totalWins}</h2>
          <MatchDetailCard teamName ={team.teamName} match={team.matchList[0]}/>
          {team.matchList.slice(1).map(matObj=><MatchSmallCard teamName={team.teamName} match={matObj} />)}
        </div>
        );
}
