import {Link} from 'react-router-dom';
//import './MatchDetailCard.scss';

export function MatchDetailCard({match,teamName}) {

    const otherTeam = (match.team1===teamName)?match.team2:match.team1;
    const otherTeamPath = `/team/${otherTeam}`;

    const isMatchWon = teamName===match.matchwinner;


  return (
    <div className={ isMatchWon ? "MatchDetailCard won-card" : "MatchDetailCard lost-card"}>
        <div>
            <span className="vs">vs</span>
            <h2><Link to={otherTeamPath}>{otherTeam}</Link></h2>
            <h2 className="match-date">{match.date}</h2>
            <h2 className="match-venue">{match.venue}</h2>
            <h2 className="match-result">{match.matchwinner} won by {match.result_margin} {match.result}</h2>
        </div>
        <div className="additional-detail">
            <h3>First Innings</h3>
            <p>{match.team1}</p>
            <h3>Second Innings</h3>
            <p>{match.team2}</p>
            <h3>Man Of The Match</h3>
            <p>{match.manOfTheMatch}</p>
            <h3>Umpires</h3>
            <p>{match.umpire1}, {match.umpire2}</p>
        </div>
    </div>
  );
  }
